(function(){var app=angular.module("app");app.controller("EmailFormController",["$http","$scope","constants","errorLog",function($http,$scope,constants,errorLog){var model=this;model.name="";model.email="";model.phone="";model.message="";model.waiting=false;model.clear=function(){model.name="";model.email="";model.phone="";model.message="";model.waiting=false;$scope.mailForm.$setPristine()};model.send=function(to){model.to=to;$http({method:"POST",url:constants.server+"/correo/",data:model,beforeSend:function(){model.waiting=true},complete:function(){model.waiting=false}}).then(function(){alert("Gracias, su mensage fué enviado con éxito. Prónto nos comunicarémos con usted.");model.clear()},errorLog)}}])})();